import styled from "styled-components";
import { FiActivity } from "react-icons/fi";

const Wrapper = styled.div`
  flex: 1;
  min-width: 100%;
`;

// const getMedian = (values) => {
//   values.sort((a, b) => a - b);
//   const half = Math.floor(values.length / 2);
//   if (values.length % 2) return values[half];
//   return (values[half - 1] + values[half]) / 2.0;
// };

function median(arr) {
  return arr
    .slice(1)
    .map((n, i) => n - arr[i])
    .reduce((acc, sum) => acc + sum);
}

const Stats = ({ data, selectedId }) => {
  const isDisabled = selectedId === null;

  const getTrajectoryLength = () => {
    const timeValues = data[selectedId]?.points.map(({ time }) => time);
    const diff = Math.max(...timeValues) - Math.min(...timeValues);
    return diff;
  };

  const getMedianSpeed = () => {
    let timeValues = [];
    let xValues = [];
    let yValues = [];

    data[selectedId]?.points.forEach(({ time, x, y }) => {
      timeValues = [...timeValues, time];
      xValues = [...xValues, x];
      yValues = [...yValues, y];
    });

    const medianSpeed = getMedian(timeValues);

    // const medianSpeed = median(timeValues);
    const medianX = median(xValues);
    const medianY = median(yValues);

    return JSON.stringify({ medianSpeed, medianX, medianY });
  };

  //   function diff(A) {
  //     return A.slice(1).map(function(n, i) {
  //        return n - A[i];
  //     })
  //     }

  //  console.log(diff(arr))
  //  console.log(diff(arr).reduce((acc, sum) => acc + sum))

  return (
    <Wrapper>
      <h2>
        <FiActivity />
        <span className="mobile-hide">Did you know ?</span>
      </h2>
      {isDisabled ? (
        "Select a profile to know more about him"
      ) : (
        <>
          <span>Durée du parcours: {getTrajectoryLength()}s</span>
          <span>Vitesse moyenne: {getMedianSpeed()}</span>
          <span>Nombre d'arrêts:</span>
        </>
      )}
    </Wrapper>
  );
};

export default Stats;
