import { useRef } from "react";
import styled from "styled-components";
import { FiEye } from "react-icons/fi";
import DataStatus from "./DataStatus";
import { Line } from "react-chartjs-2";
import { Chart } from "chart.js";

const COLORS = ["#55efc4", "#81ecec", "#74b9ff", "#a29bfe", "#fd79a8"];

// Sort data by time key
const orderByTime = (data) =>
  data.map(({ id, points }) => {
    const sortedPoints = points.sort((a, b) => a.time - b.time);
    return { id, points: sortedPoints };
  });

const calcCoordinates = (data) => {
  const orderedData = orderByTime(data);

  return orderedData.map(({ id, points }) => {
    const timeValues = points.map((value) => value.time);
    var ratio = Math.max(...timeValues) / 100;
    // var ratio = Math.max(...timeValues) / 100;

    // normalize coordinates values with ratio
    const normalizePoints = points.map((coordinates) => ({
      x: coordinates.x / ratio,
      y: coordinates.y / ratio,
    }));

    return { id, points: normalizePoints };
  });
};

const LineChart = ({ data }) => {
  const coordinates = calcCoordinates(data);
  console.log(coordinates);

  const dataTest = {
    datasets: coordinates.map((path, index) => ({
      label: `Trajectory of ${path.id}`,
      data: path.points,
      fill: false,
      backgroundColor: COLORS[index],
      borderColor: COLORS[index],
    })),
  };

  const options = {
    lineTension: 0.5,
    scales: {
      x: {
        type: "linear",
      },
    },
  };

  return <Line data={dataTest} options={options} />;
};

const MainBase = styled.div`
  padding: 2em;
  color: snow;
`;

const Header = styled.div`
  display: flex;
  align-items: center;
  justify-content: space-between;
`;

const Logo = styled.h1`
  display: flex;
  align-items: center;
`;

const Content = styled.div`
  display: flex;
  justify-content: space-between;

  & > div {
    display: flex;
    flex-direction: column;

    border-radius: 6px;
    background: rgba(0, 0, 0, 0.4);
  }
`;

const Persons = styled.div`
  width: 10%;
  margin-right: 2em;
`;

const Trajectories = styled.div`
  width: 90%;
`;

// function clickHandler(evt) {
