//     evt,
//     "nearest",
//     { intersect: true },
//     true
//   );

//   if (points.length) {
//     const firstPoint = points[0];
//     const label = myChart.data.labels[firstPoint.index];
//     const value =
//       myChart.data.datasets[firstPoint.datasetIndex].data[firstPoint.index];
//   }
// }

const Main = ({ data = [] }) => {
  const lineChartRef = useRef();

  const toggleVisibility = () => {
    if (lineChartRef.current) return null;

    // console.log("exist");
    // const chart = Chart.getChart(lineChartRef.current);
    // chart.setDatasetVisibility(1, false); // hides dataset at index 1
    // chart.update(); // chart now renders with dataset hidden
  };

  return (
    <MainBase>
      <Header>
        <Logo>
          <FiEye style={{ marginRight: "10px" }} />
          <span>Lil Brother</span>
        </Logo>
        <DataStatus data={data} />
      </Header>

      <Content>
        <Persons>
          <h2>Persons</h2>
          <ul style={{ listStyle: "none" }}>
            {data.map((person, index) => (
              <li key={index}>
                <button onClick={toggleVisibility}>{person.id}</button>
              </li>
            ))}
          </ul>
        </Persons>
        <Trajectories>
          <h2>Trajectories</h2>
          <LineChart ref={lineChartRef} data={data} />
        </Trajectories>
      </Content>

      {data.map((item, index) => (
        <div key={index}>{item.id}</div>
      ))}

      {JSON.stringify(data)}
    </MainBase>
  );
};

export default Main;
