import { useRef } from "react";
import styled, { keyframes } from "styled-components";
import { FiEye } from "react-icons/fi";
import DataStatus from "./DataStatus";
import { Line } from "react-chartjs-2";
import { Chart } from "chart.js";

const COLORS = ["#00b894", "#74b9ff", "#fdcb6e", "#6c5ce7", "#ff7675"];

const blink = keyframes`
  from {
    transform: scaleY(0.1);
  }
  to {
    transform: rotate(1);
`;

// Sort data by time key
const orderByTime = (data) =>
  data.map(({ id, points }) => {
    const sortedPoints = points.sort((a, b) => a.time - b.time);
    return { id, points: sortedPoints };
  });

// const calcCoordinates = (data) => {
//   const orderedData = orderByTime(data);

//   return orderedData.map(({ id, points }) => {
//     const timeValues = points.map((value) => value.time);
//     var ratio = Math.max(...timeValues) / 100;
//     // var ratio = Math.max(...timeValues) / 100;

//     // normalize coordinates values with ratio
//     const normalizePoints = points.map((coordinates) => ({
//       x: coordinates.x / ratio,
//       y: coordinates.y / ratio,
//     }));

//     return { id, points: normalizePoints };
//   });
// };

const LineChart = ({ data }) => {
  // const coordinates = calcCoordinates(data);
  // console.log(coordinates);
  const orderedData = orderByTime(data);
  // console.log(coordinates);

  const dataTest = {
    datasets: orderedData.map((path, index) => ({
      label: `Trajectory of ${path.id}`,
      data: path.points,
      fill: false,
      backgroundColor: COLORS[index],
      borderColor: COLORS[index],
    })),
  };

  const options = {
    lineTension: 0.5,
    scales: {
      x: {
        type: "linear",
      },
    },
  };

  return <Line data={dataTest} options={options} />;
};

const MainBase = styled.div`
  padding: 2em;
  color: snow;
`;

const Header = styled.div`
  display: flex;
  align-items: center;
  justify-content: space-between;
`;

const Title = styled.h1`
  display: flex;
  align-items: center;
`;

const Logo = styled.FiEye`
  display: flex;
  align-items: center;
`;

const Content = styled.div`
  display: flex;
  justify-content: space-between;

  & > div {
    display: flex;
    flex-direction: column;

    border-radius: 6px;
    background: rgba(0, 0, 0, 0.4);
  }
`;

const Persons = styled.div`
  width: 10%;
  margin-right: 2em;
`;

const Trajectories = styled.div`
  width: 90%;
`;

// function clickHandler(evt) {
//   const points = myChart.getElementsAtEventForMode(
//     evt,
//     "nearest",
//     { intersect: true },
//     true
//   );

//   if (points.length) {
//     const firstPoint = points[0];
//     const label = myChart.data.labels[firstPoint.index];
//     const value =
//       myChart.data.datasets[firstPoint.datasetIndex].data[firstPoint.index];
//   }
// }

let chartInstance;

const Main = ({ data = [] }) => {
  const lineChartRef = useRef();

  return (
    <MainBase>
      <Header>
        <Title>
          <FiEye style={{ marginRight: "10px" }} />
          <span>Lil Brother</span>
        </Title>
        <DataStatus data={data} />
      </Header>

      <Content>
        <Persons>
          <h2>Persons</h2>

          {data.map((person, index) => (
            // <li key={index}>
            <button>{person.id}</button>
          ))}
        </Persons>
        <Trajectories>
          <h2>Trajectories</h2>
          <LineChart ref={lineChartRef} data={data} />
        </Trajectories>
      </Content>

      {data.map((item, index) => (
        <div key={index}>{item.id}</div>
      ))}

      {JSON.stringify(data)}
    </MainBase>
  );
};

export default Main;
