import styled from "styled-components";
import { FiEye } from "react-icons/fi";
import DataStatus from "./DataStatus";

import { Line } from "react-chartjs-2";

const dataTest = {
  labels: ["1", "2", "3", "4", "5", "6"],
  datasets: [
    {
      label: "# of Votes",
      data: [12, 19, 3, 5, 2, 3],
      fill: false,
      backgroundColor: "rgb(255, 99, 132)",
      borderColor: "rgba(255, 99, 132, 0.2)",
    },
  ],
};

const totalDuration = 10000;
const delayBetweenPoints = totalDuration / dataTest.length;
const previousY = (ctx) =>
  ctx.index === 0
    ? ctx.chart.scales.y.getPixelForValue(100)
    : ctx.chart
        .getDatasetMeta(ctx.datasetIndex)
        .data[ctx.index - 1].getProps(["y"], true).y;

const animation = {
  x: {
    type: "number",
    easing: "linear",
    duration: delayBetweenPoints,
    from: NaN, // the point is initially skipped
    delay(ctx) {
      if (ctx.type !== "data" || ctx.xStarted) {
        return 0;
      }
      ctx.xStarted = true;
      return ctx.index * delayBetweenPoints;
    },
  },
  y: {
    type: "number",
    easing: "linear",
    duration: delayBetweenPoints,
    from: previousY,
    delay(ctx) {
      if (ctx.type !== "data" || ctx.yStarted) {
        return 0;
      }
      ctx.yStarted = true;
      return ctx.index * delayBetweenPoints;
    },
  },
};

const options = {
  animation,
  lineTension: 0.5,
  scales: {
    x: {
      type: "linear",
    },
    y: {
      beginAtZero: true,
    },
  },
};

const LineChart = () => <Line data={dataTest} options={options} />;

const MainBase = styled.div`
  padding: 2em;
  color: snow;
`;

const Header = styled.div`
  display: flex;
  align-items: center;
  justify-content: space-between;
`;

const Logo = styled.h1`
  display: flex;
  align-items: center;
`;

const Content = styled.div`
  display: flex;
  justify-content: space-between;

  & > div {
    display: flex;
    flex-direction: column;

    border-radius: 6px;
    background: rgba(0, 0, 0, 0.4);
  }
`;

const Persons = styled.div`
  width: 10%;
  margin-right: 2em;
`;

const Trajectories = styled.div`
  width: 90%;
`;

const Main = ({ data = [] }) => {
  return (
    <MainBase>
      <Header>
        <Logo>
          <FiEye style={{ marginRight: "10px" }} />
          <span>Lil Brother</span>
        </Logo>
        <DataStatus data={data} />
      </Header>

      <Content>
        <Persons>
          <h2>Persons</h2>
          <ul style={{ listStyle: "none" }}>
            {data.map((person) => (
              <li>
                <button>{person.id}</button>
              </li>
            ))}
          </ul>
        </Persons>
        <Trajectories>
          <h2>Trajectories</h2>
          <LineChart />
        </Trajectories>
      </Content>

      {data.map((item) => (
        <div>{item.id}</div>
      ))}

      {JSON.stringify(data)}
    </MainBase>
  );
};

export default Main;
