import styled from "styled-components";
import { FiActivity } from "react-icons/fi";

const Wrapper = styled.div`
  flex: 1;
  min-width: 100%;
`;

const getMedian = (values) => {
  values.sort((a, b) => a - b);
  const half = Math.floor(values.length / 2);
  if (values.length % 2) return values[half];
  return (values[half - 1] + values[half]) / 2.0;
};

const Stats = ({ data, selectedId }) => {
  const isDisabled = selectedId === null;

  const getTrajectoryLength = () => {
    const timeValues = data[selectedId]?.points.map(({ time }) => time);
    const diff = Math.max(...timeValues) - Math.min(...timeValues);
    return diff;
  };

  const getMedianSpeed = () => {
    let timeValues = [];
    let xValues = [];
    let yValues = [];

    // const timeValues = data[selectedId]?.points.map(({ time }) => time);
    // const xValues = data[selectedId]?.points.map(({ x }) => x);
    // const yValues = data[selectedId]?.points.map(({ y }) => y);

    data[selectedId]?.points.forEach(({ time, x, y }) => {
      timeValues = [...timeValues, time];
      xValues = [...xValues, x];
      yValues = [...yValues, y];
    });

    const medianSpeed = getMedian(timeValues);
    const medianX = getMedian(xValues);
    const medianY = getMedian(yValues);

    console.log({ medianSpeed, medianX, medianY });

    function diff(arr) {
      return arr
        .slice(1)
        .map((n, i) => n - arr[i])
        .reduce((acc, sum) => acc + sum);
    }

    function test(arr) {
      return arr.slice(1).map((n, i) => n - arr[i]);
    }

    console.log("test", test(timeValues));

    const medianSpeed2 = diff(timeValues);
    const medianX2 = diff(xValues);
    const medianY2 = diff(yValues);

    console.log({ medianSpeed2, medianX2, medianY2 });
  };

  //   function diff(A) {
  //     return A.slice(1).map(function(n, i) {
  //        return n - A[i];
  //     })
  //     }

  //  console.log(diff(arr))
  //  console.log(diff(arr).reduce((acc, sum) => acc + sum))

  return (
    <Wrapper>
      <h2>
        <FiActivity />
        <span className="mobile-hide">Did you know ?</span>
      </h2>
      {isDisabled ? (
        "Select a profile to know more about him"
      ) : (
        <>
          <span>Dur√©e du parcours: {getTrajectoryLength()}s</span>
          <span>Vitesse moyenne: {getMedianSpeed()}</span>
          <span>Nombre d'arr√™ts:</span>
        </>
      )}
    </Wrapper>
  );
};

export default Stats;
