import { forwardRef } from "react";
import styled from "styled-components";
import { FiGitBranch } from "react-icons/fi";
import { COLORS } from "../utils/constants";
import { Line } from "react-chartjs-2";

const Wrapper = styled.div`
  display: flex;
  // flex: 1/2;
  // width: 90%;
`;

const orderByTime = (data) =>
  data.map(({ id, points }) => {
    const sortedPoints = points.sort((a, b) => a.time - b.time);
    return { id, points: sortedPoints };
  });

const LineChart = forwardRef(({ data, onClick }, ref) => {
  const orderedData = orderByTime(data);

  const dataTest = {
    datasets: orderedData.map((path, index) => ({
      label: `Trajectory of ${path.id}`,
      data: path.points,
      fill: false,
      backgroundColor: COLORS[index],
      borderColor: COLORS[index],
    })),
  };

  const options = {
    lineTension: 0.5,
    scales: {
      x: {
        type: "linear",
      },
    },
  };

  return <Line ref={ref} data={dataTest} options={options} onClick={onClick} />;
});

const Trajectories = ({ data, innerRef }) => (
  <Wrapper>
    <h2>
      <FiGitBranch />
      Trajectories
    </h2>
    <LineChart ref={innerRef} data={data} />
  </Wrapper>
);

export default Trajectories;
