import { useRef, forwardRef } from "react";
import styled, { keyframes } from "styled-components";
import { FiEye, FiUser, FiUsers, FiGitBranch } from "react-icons/fi";
import DataStatus from "./DataStatus";
import { Line } from "react-chartjs-2";

const COLORS = ["#00b894", "#74b9ff", "#fdcb6e", "#6c5ce7", "#ff7675"];

const blink = keyframes`
  0% {}
  1% { transform: scaleY(1); }
  3% { transform: scaleY(0.2); }
  5% { transform: scaleY(1); }
  6% { transform: scaleY(1); }
  8% { transform: scaleY(0.2); }
  10% { transform: scaleY(1); }
`;

const fadeFromLeft = keyframes`
  from { 
    opacity: 0;
    transform: translateX(-10px) 
  }
  to {
    opacity: 1;
    transform: translateX(0) 
  }
`;

const MainBase = styled.div`
  padding: 2em;
  color: snow;
`;

const Header = styled.div`
  display: flex;
  align-items: center;
  justify-content: space-between;
`;

const Title = styled.h1`
  display: flex;
  align-items: center;
`;

const Logo = styled(FiEye)`
  margin-right: 10px;
  animation: ${blink} 5s infinite ease-in-out 2s;
`;

const Content = styled.div`
  display: flex;
  justify-content: space-between;

  & > div {
    display: flex;
    flex-direction: column;

    border-radius: 6px;
    background: rgba(0, 0, 0, 0.4);
    padding: 1em;
  }

  & h2 {
    display: flex;
    align-items: center;
    margin: 0;
    margin-bottom: 1em;

    & > svg {
      margin-right: 6px;
    }
  }
`;

const People = styled.div`
  width: 10%;
  margin-right: 2em;
`;

const Trajectories = styled.div`
  width: 90%;
`;

// Sort data by time key
const orderByTime = (data) =>
  data.map(({ id, points }) => {
    const sortedPoints = points.sort((a, b) => a.time - b.time);
    return { id, points: sortedPoints };
  });

const LineChart = forwardRef(({ data, onClick }, ref) => {
  const orderedData = orderByTime(data);

  const dataTest = {
    datasets: orderedData.map((path, index) => ({
      label: `Trajectory of ${path.id}`,
      data: path.points,
      fill: false,
      backgroundColor: COLORS[index],
      borderColor: COLORS[index],
    })),
  };

  const options = {
    lineTension: 0.5,
    scales: {
      x: {
        type: "linear",
      },
    },
  };

  return <Line ref={ref} data={dataTest} options={options} onClick={onClick} />;
});

const PeopleWrapper = styled.div`
  display: flex;
  flex-direction: column;
`;

const PeopleButton = styled.button`
  display: inline-flex;
  justify-content: center;
  align-items: center;
  flex-direction: row;

  pborder: 0;
  padding: 0;
  border: 0;
  background: ${({ color }) => color};
  cursor: pointer;
  padding: 0.5em;
  border-radius: 4px;
  color: ${({ color }) => (color ? "snow" : "#34495e")};

  font-size: 14px;
  font-weight: 600;
  vertical-align: middle;
  transition: opacity 0.3s ease;
  animation: ${fadeFromLeft} 0.5s ease-in-out
    ${({ index }) => (index ? index / 10 : 0)}s;

  opacity: 0;

  animation-fill-mode: both;
  &:hover {
    opacity: 0.8;
  }
  &:active {
    transform: scale(0.98);
  }

  & > span {
    margin: 0 auto;
  }

  :not(:first-child) {
    margin-bottom: 4px;
  }
`;

const Separator = styled.hr`
  width: 100%;
  margin: 0 auto;
  opacity: 0.2;
`;

const Main = ({ data = [] }) => {
  const lineChartRef = useRef();

  const resetTrajectories = () =>
    data.forEach((_person, idx) => lineChartRef.current.show(idx));

  const showTrajectory = (index) => {
    data.forEach((_person, idx) => {
      idx !== index
        ? lineChartRef.current.hide(idx)
        : lineChartRef.current.show(index);
    });
  };

  return (
    <MainBase>
      <Header>
        <Title>
          <Logo />
          <span>Lil Brother</span>
        </Title>
        <DataStatus data={data} />
      </Header>

      <Content>
        <People>
          <h2>
            <FiUsers />
            People
          </h2>

          <PeopleWrapper>
            <PeopleButton onClick={resetTrajectories}>All</PeopleButton>
            <Separator />
            {data.map((person, index) => (
              <PeopleButton
                key={index}
                index={index}
                onClick={() => showTrajectory(index)}
                color={COLORS[index]}
              >
                <FiUser /> <span>{person.id}</span>
              </PeopleButton>
            ))}
          </PeopleWrapper>
        </People>
        <Trajectories>
          <h2>
            <FiGitBranch />
            Trajectories
          </h2>
          <LineChart ref={lineChartRef} data={data} />
        </Trajectories>
      </Content>

      {JSON.stringify(data)}
    </MainBase>
  );
};

export default Main;
