import { useRef, forwardRef } from "react";
import styled, { keyframes } from "styled-components";
import { FiEye, FiUsers, FiGitBranch } from "react-icons/fi";
import DataStatus from "./DataStatus";
import { Line } from "react-chartjs-2";

const COLORS = ["#00b894", "#74b9ff", "#fdcb6e", "#6c5ce7", "#ff7675"];

const blink = keyframes`
  0% {}
  1% { transform: scaleY(1); }
  3% { transform: scaleY(0.2); }
  5% { transform: scaleY(1); }
  6% { transform: scaleY(1); }
  8% { transform: scaleY(0.2); }
  10% { transform: scaleY(1); }
`;

const MainBase = styled.div`
  padding: 2em;
  color: snow;
`;

const Header = styled.div`
  display: flex;
  align-items: center;
  justify-content: space-between;
`;

const Title = styled.h1`
  display: flex;
  align-items: center;
`;

const Logo = styled(FiEye)`
  margin-right: 10px;
  animation: ${blink} 5s infinite ease-in-out 2s;
`;

const Content = styled.div`
  display: flex;
  justify-content: space-between;

  & > div {
    display: flex;
    flex-direction: column;

    border-radius: 6px;
    background: rgba(0, 0, 0, 0.4);
    padding: 1em;
  }

  & h2 {
    display: flex;
    align-items: center;
    margin: 0;
    margin-bottom: 1em;

    & > svg {
      margin-right: 10px;
    }
  }
`;

const People = styled.div`
  width: 10%;
  margin-right: 2em;
`;

const Trajectories = styled.div`
  width: 90%;
`;

// Sort data by time key
const orderByTime = (data) =>
  data.map(({ id, points }) => {
    const sortedPoints = points.sort((a, b) => a.time - b.time);
    return { id, points: sortedPoints };
  });

const LineChart = forwardRef(({ data, onClick }, ref) => {
  const orderedData = orderByTime(data);

  const Label = ({ id }) => <>Trajectory of {id}</>;

  const dataTest = {
    datasets: orderedData.map((path, index) => ({
      label: `Trajectory of ${path.id}`,
      data: path.points,
      fill: false,
      backgroundColor: COLORS[index],
      borderColor: COLORS[index],
    })),
  };

  const options = {
    lineTension: 0.5,
    scales: {
      x: {
        type: "linear",
      },
    },
  };

  return <Line ref={ref} data={dataTest} options={options} onClick={onClick} />;
});

// function clickHandler(evt) {
//   const points = myChart.getElementsAtEventForMode(
//     evt,
//     "nearest",
//     { intersect: true },
//     true
//   );

//   if (points.length) {
//     const firstPoint = points[0];
//     const label = myChart.data.labels[firstPoint.index];
//     const value =
//       myChart.data.datasets[firstPoint.datasetIndex].data[firstPoint.index];
//   }
// }

const Main = ({ data = [] }) => {
  const lineChartRef = useRef();

  const resetTrajectories = () =>
    data.forEach((_person, idx) => lineChartRef.current.show(idx));

  const showTrajectory = (index) => {
    data.forEach((_person, idx) => {
      idx !== index
        ? lineChartRef.current.hide(idx)
        : lineChartRef.current.show(index);
    });
  };

  return (
    <MainBase>
      <Header>
        <Title>
          <Logo />
          <span>Lil Brother</span>
        </Title>
        <DataStatus data={data} />
      </Header>

      <Content>
        <People>
          <h2>
            <FiUsers />
            People
          </h2>

          <button onClick={resetTrajectories}>All</button>
          {data.map((person, index) => (
            <button key={index} onClick={() => showTrajectory(index)}>
              {person.id}
            </button>
          ))}
        </People>
        <Trajectories>
          <h2>
            <FiGitBranch />
            Trajectories
          </h2>
          <LineChart ref={lineChartRef} data={data} />
        </Trajectories>
      </Content>

      {JSON.stringify(data)}
    </MainBase>
  );
};

export default Main;
